<?php


require_once dirname(__FILE__).'/libs/config.php';
require_once dirname(__FILE__).'/libs/api_libs.php';
require_once dirname(__FILE__).'/libs/fetch_camera_picture.php';

//error_reporting(E_ALL);
//ini_set('display_errors','1');

$user = null;

if(!empty($_GET['username'])){

	$api_action = 'camera';

	$authentication_error = checkAPILoginAuthenticationError($_GET['username'], $_GET['password'], $_SERVER['REMOTE_ADDR'], $api_action, $user);


	if(!$authentication_error && !$user['has_camera_access']){
		$authentication_error = 'User does not have camera access';
	}

	if($authentication_error){
		_log_authentication_error($api_action, $authentication_error);
	}

}else{
	_require_login();

	$user = _get_user_array();

}

$camera1 = $_GET['camera'];
$camera2 = null;

if(!empty($_GET['camera2']))
	$camera2 = $_GET['camera2'];

if(!$user['has_camera_access']){
	echo 'Not authorized';
	exit;
}

$camera_sql = 'SELECT last_fetched, stream_url, stream_login, name_id, permission_required FROM cameras WHERE name_id=? LIMIT 1';

$cameras = array();

$cameras[1] = $db->queryfirst($camera_sql, $camera1);


if(!_check_permission($cameras[1]['permission_required'], $user)){
	echo 'Not authorized';
	exit;
}

if(!empty($camera2)) {
	$cameras[2] = $db->queryfirst($camera_sql, $camera2);

	if(!_check_permission($cameras[2]['permission_required'], $user)){
		echo 'Not authorized';
		exit;
	}

}

if(empty($cameras[1])){
	echo "No camera data";
	exit;
}


$photo_data = null;

foreach ($cameras as $camera_i => $camera) {
	// disabled for now
	if (false && $user['id'] != 1) {
		$fetches_in_5min = $db->fetch('SELECT COUNT(*) FROM camera_logs WHERE user_id=# AND camera_name_id=? AND time > DATE_SUB(NOW(), INTERVAL 5 MINUTE )', $user['id'], $camera['name_id']);

		if ($fetches_in_5min > 150) {
			header('Content-type: image/png');
			echo base64_decode('');
			exit;
		}
	}


	if ( (time() - strtotime($camera['last_fetched'])) >= 1) {
		// fetch new photo
		$cameras[$camera_i]['photo_data'] = fetchCameraPicture($camera['stream_url'], $camera['stream_login']);

		$db->query('UPDATE cameras SET data_jpg=??, last_fetched = NOW() WHERE name_id=?', $cameras[$camera_i]['photo_data'], $camera['name_id']);
		$db->query('INSERT INTO camera_logs SET user_id=#, camera_name_id=?, time=NOW()', $user['id'], $camera['name_id']);
	} else {
		// show old photo
		$cameras[$camera_i]['photo_data'] = $db->fetch('SELECT data_jpg FROM cameras WHERE name_id=? LIMIT 1', $camera['name_id']);
	}

}

if(!empty($cameras[2]['photo_data'])){

	$img1 = imagecreatefromstring($cameras[1]['photo_data']);
	list($width, $height, $type, $attr) =  getimagesizefromstring($cameras[1]['photo_data']);

	$thumb = imagecreatetruecolor($width, $height*2);
	$img1 = imagecreatefromstring($cameras[1]['photo_data']);
	$img2 = imagecreatefromstring($cameras[2]['photo_data']);

	imagecopy($thumb, $img1, 0, 0, 0, 0, $width, $height); //have to play with these numbers for it to work for you, etc.
	imagecopy($thumb, $img2, 0, $height, 0, 0, $width, $height); //have to play with these numbers for it to work for you, etc.
	header('Content-type: image/jpeg');

	imagejpeg($thumb);
	exit;

}else{
	$photo_data = $cameras[1]['photo_data'];

	if($photo_data) {
		header('Content-type: image/jpeg');
		echo $photo_data;
	}else{
		echo "UNKNOWN ERROR";
	}
}








